import React, { useState }  from "react";
import './verify.css';
import './signupU';
import Signup from "./signupU";
import { BrowserRouter, Routes, Route } from "react-router-dom";

export default function Signin(props){

    function switch_screen(){
        props.setSignin(false) 
        props.setSignup(true)
    }

    return(props.signin) ? (
        <div className="verify-form">   
            <div className="verify-form__container">   
                <div className="verify-form__header">
                <button onClick={() => switch_screen()}  className="verify-form__btn">Đăng ký</button> 
                <Signup />

                <h3 className="verify-form__heading">Đăng nhập</h3>
                { props.children }
                </div>
                <div className="verify-form__form">
                    <div className="verify-form-group">
                        <input className="verify-form__input" placeholder="Email"/>
                    </div>
                    <div className="verify-form-group">setSignin
                        <input className="verify-form__input" placeholder="SĐT"/>
                    </div>        
                </div>

                <div className="verify-form__controls">   
                    <button onClick={() => props.setSignin(false)} className="verify-form__controls-back btn">Trở lại</button>
                    <button className="verify-form__controls-DK btn--DK">Đăng nhập</button>
                </div>
            </div>


        </div>
    ) : "";
}
