import logo from './logo.svg';
import './App.css';
import './css/fonts/fontawesome-free-6.2.0/css/all.min.css'
import React, { useState } from 'react';
import Signup from './signupU';
import Signin from './signin';


function App() {
  const [Signin1,setSignin] = useState(false);
  const [Signup1,setSignup] = useState(false);

  function shutdownU(){
    setSignup(true)
    if(Signup1 === true || Signin1 === true){
      setSignin(false)
      setSignup(false)
    }
  }

  return (
    <div className="App">

      <button onClick={() => shutdownU()} className='App-login'>
        <div className='App-login-text'> 
            Người dùng
        </div>
        <i className='App-login-icon fa-solid fa-user'></i>
      </button>

      <Signin signin={Signin1}  setSignin={setSignin} setSignup={setSignup}/>
      <Signup signup={Signup1}  setSignin={setSignin} setSignup={setSignup}/>     

      <button className='App-login'>
        <div className='App-login-text'> 
            Tài xế
        </div>
        <i className='App-login-icon fa-solid fa-motorcycle'></i>
      </button> 

      <button className='App-login'>
        <div className='App-login-text'> 
            Đối tác
        </div>
        <i className='App-login-icon fa-sharp fa-solid fa-shop'></i>
      </button>  
    </div>
  );
}

export default App;
