import logo from './logo.svg';
import './App.css';
import './css/fonts/fontawesome-free-6.2.0/css/all.min.css'
import React, { useState } from 'react';
import Signup from './signupU';
import Signin from './signin';
import SignupS from './signupS';
import SigninS from './signinS';
import UserScreen from "./UserScreen";
import { BrowserRouter, Routes, Route } from "react-router-dom";

function App() {
  const [SigninU,setSignin] = useState(false);
  const [SignupU,setSignup] = useState(false);

  const [SigninStore,setSigninStore] = useState(false);
  const [SignupStore,setSignupStore] = useState(false);

  function shutdownU(){
    setSignup(true)
    if(SignupU === true || SigninU === true){
      setSignin(false)
      setSignup(false)
    }
  }

  function shutdownS(){
    setSignupStore(true)
    if(SignupStore === true || SigninStore === true){
      setSigninStore(false)
      setSignupStore(false)
    }
  }

  return (
    <div className="App">

      <button onClick={() => shutdownU()} className='App-login'>
        <div className='App-login-text'> 
            Người dùng
        </div>
        <i className='App-login-icon fa-solid fa-user'></i>
      </button>

      <Signin signin={SigninU}  setSignin={setSignin} setSignup={setSignup}/>
      <Signup signup={SignupU}  setSignin={setSignin} setSignup={setSignup}/>     

      <button className='App-login'>
        <div className='App-login-text'> 
            Tài xế
        </div>
        <i className='App-login-icon fa-solid fa-motorcycle'></i>
      </button> 

      <button onClick={() => shutdownS()} className='App-login'>
        <div className='App-login-text'> 
            Đối tác
        </div>
        <i className='App-login-icon fa-sharp fa-solid fa-shop'></i>
      </button>  

      <SigninS signin={SigninStore}  setSignin={setSigninStore} setSignup={setSignupStore}/>
      <SignupS signup={SignupStore}  setSignin={setSigninStore} setSignup={setSignupStore}/>


      <BrowserRouter> 
                <Routes>    
                    <Route path="/" element={<App />}> 
                        <Route index element={<App />} />
                        <Route path="User_page" element={<UserScreen />} />
                    </Route>
                </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
