import logo from './logo.svg';
import './App.css';
import './css/fonts/fontawesome-free-6.2.0/css/all.min.css'
import { useState } from 'react';
import SignupUser from './signupU';
import Signin from './signin';
import SignupStore from './signupS';

function App() {
  const [Signin,setSignin] = useState(false);
  const [Signup,setSignup] = useState(false);


  const [SignupS,setSignupS] = useState(false);

  function shutdownU(){
    setSignup(true)
    if(Signup === true || Signin === true){
      setSignin(false)
      setSignup(false)
    }
  }

  return (
    <div className="App">

      <button onClick={() => shutdownU()} className='App-login'>
        <div className='App-login-text'> 
            Người dùng
        </div>
        <i className='App-login-icon fa-solid fa-user'></i>
      </button>
      <Signin signin={Signin}  setSignin={setSignin} setSignup={setSignup}/>
      <SignupUser signup={Signup}  setSignin={setSignin} setSignup={setSignup}/>     

      <button className='App-login'>
        <div className='App-login-text'> 
            Tài xế
        </div>
        <i className='App-login-icon fa-solid fa-motorcycle'></i>
      </button> 

      <button className='App-login'>
        <div className='App-login-text'> 
            Đối tác
        </div>
        <i className='App-login-icon fa-sharp fa-solid fa-shop'></i>
      </button>  




    </div>
  );
}

export default App;
