import React from "react";
import '../css/fonts/fontawesome-free-6.2.0/css/all.min.css'
import Items from "./item";
//import pic from '../../public/default.jpg'
//import { Outlet, Link } from "react-router-dom";

export default function Container(){

    const DisplayItem = props =>(
        <div className="cover">   
            <div className="img">
                <img src="../../public/default.jpg" alt="picture of food"/>
            </div>

            <div className="detail">

                <div className="detail-items">
                    <p className="Title">Tên món ăn:</p>
                    <p className="value">{props.name}</p>
                </div>

                <div className="detail-items">
                    <p className="Title">Mô tả:</p>
                    <p className="value">{props.des}</p>
                </div>

                <div className="detail-items">
                    <p className="Title">Nhận xét:</p>
                    <p className="value">{props.compliment}</p>
                </div>

                <div className="detail-items">
                    <p className="Title">Giá:</p>
                    <p className="value">{props.price}</p>
                </div>

            </div>
        </div>
    )

    return(
        <div className="display">
            {Items.map(
                item => (
                <DisplayItem 
                    key = {item.id}
                    img = {item.linkImg}
                    name = {item.foodName}
                    des = {item.description}
                    compliment = {item.judge}
                    price = {item.price}
                />
            ))}
        </div>
    );
}
